/* edit-appointment.component.css */
.edit-appointment-container {
  display: flex;
  min-height: 100vh;
  background-color: #f4f6f8;
}

.main-content {
  flex-grow: 1;
  padding: 3em;
  margin-left: 20%; /* Ensure space for the fixed sidebar */
}

h2 {
  color: #333;
  margin-bottom: 1.5em;
}

.form-group {
  margin-bottom: 1.2em;
}

.form-group label {
  display: block;
  margin-bottom: 0.5em;
  font-weight: bold;
  color: #555;
}

.form-group input[type="text"],
.form-group input[type="date"],
.form-group input[type="time"],
.form-group textarea {
  width: 100%;
  padding: 0.8em;
  border: 1px solid #ccc;
  border-radius: 0.3em;
  font-size: 1em;
  box-sizing: border-box;
}

.form-group textarea {
  min-height: 80px;
}

.form-actions {
  margin-top: 2em;
}

.cancel-button,
.save-button {
  padding: 0.8em 1.5em;
  border-radius: 0.3em;
  cursor: pointer;
  font-size: 1em;
  transition: background-color 0.3s ease;
}

.cancel-button {
  background-color: #f44336;
  color: white;
  border: none;
  margin-right: 1em;
}

.cancel-button:hover {
  background-color: #d32f2f;
}

.save-button {
  background-color: #4caf50;
  color: white;
  border: none;
}

.save-button:hover {
  background-color: #45a049;
}
