commits.content:
.login-container {
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background-color: #f0f2f5; /* Un fondo gris claro */
}

.login-card {
  background-color: #fff;
  padding: 30px;
  border-radius: 8px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  width: 400px;
  max-width: 90%;
}

.login-header {
  text-align: center;
  margin-bottom: 25px;
}

.login-header h1 {
  color: #1877f2; /* Un azul de Facebook similar */
  margin-bottom: 5px;
}

.login-header p {
  color: #657786; /* Un gris azulado */
  font-size: 0.9em;
}

.login-form {
  display: flex;
  flex-direction: column;
}

.form-group {
  margin-bottom: 20px;
}

.form-group label {
  display: block;
  margin-bottom: 5px;
  color: #1c1e21; /* Un negro suave */
  font-weight: bold;
}

.form-group input[type="email"],
.form-group input[type="password"] {
  width: 100%;
  padding: 12px;
  border: 1px solid #ddd;
  border-radius: 6px;
  font-size: 1em;
  box-sizing: border-box;
}

.password-input-container {
  position: relative;
  display: flex;
  align-items: center;
}

.password-input-container input[type="password"] {
  padding-right: 40px; /* Espacio para el botón de mostrar/ocultar */
}

.password-toggle-button {
  position: absolute;
  right: 10px;
  top: 50%;
  transform: translateY(-50%);
  background: none;
  border: none;
  cursor: pointer;
  color: #657786;
  font-size: 1.2em;
}

.forgot-password {
  display: block;
  text-align: right;
  color: #1877f2;
  text-decoration: none;
  margin-bottom: 20px;
  font-size: 0.9em;
}

.forgot-password:hover {
  text-decoration: underline;
}

.login-button {
  background-color: #1877f2;
  color: #fff;
  padding: 14px;
  border: none;
  border-radius: 6px;
  font-size: 1.1em;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.login-button:hover {
  background-color: #1665c8;
}

.login-footer {
  text-align: center;
  margin-top: 25px;
  font-size: 0.9em;
  color: #657786;
}

.register-link {
  color: #1877f2;
  text-decoration: none;
  font-weight: bold;
}

.register-link:hover {
  text-decoration: underline;
}
