.home-container {
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background-image: linear-gradient(to bottom, #e0f7fa, #b2ebf2); /* Replicando el gradiente azul */
  padding: 20px; /* Añadido un poco de padding general */
}

.home-header {
  text-align: center;
  margin-bottom: 40px;
}

.home-header h1 {
  font-size: 2.5rem;
  font-weight: bold;
  color: #1e88e5; /* Azul principal */
  margin-bottom: 0.5rem;
}

.home-header p {
  color: #757575; /* Gris secundario */
  font-size: 1rem;
}

.home-options {
  display: flex;
  gap: 2rem;
}

.option-card {
  background-color: #fff;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1); /* Sombra suave */
  border-radius: 0.75rem; /* Bordes redondeados */
  padding: 2rem;
  width: 16rem; /* Ancho similar a w-64 */
  text-align: center;
}

.icon-svg {
  width: 3rem; /* Tamaño similar a h-12 y w-12 */
  height: 3rem;
  margin: 0 auto 1rem;
}

.patient-icon .icon-svg {
  fill: none;
  stroke: #1e88e5; /* Azul para el icono de paciente */
  stroke-width: 1.5;
}

.doctor-icon .icon-svg {
  fill: none;
  stroke: #4caf50; /* Verde para el icono de doctor */
  stroke-width: 1.5;
}

.card-title {
  font-size: 1.25rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
  color: #263238; /* Gris oscuro para el título */
}

.card-description {
  color: #757575;
  margin-bottom: 1rem;
  font-size: 0.9rem;
}

.access-link {
  display: inline-block;
  color: #1e88e5; /* Azul para el enlace de paciente */
  font-weight: 500;
  text-decoration: none;
}

.patient-link:hover {
  color: #1565c0; /* Azul más oscuro al pasar el ratón */
}

.doctor-link {
  color: #4caf50; /* Verde para el enlace de doctor */
}

.doctor-link:hover {
  color: #388e3c; /* Verde más oscuro al pasar el ratón */
}

.home-footer {
  color: #757575;
  margin-top: 2rem;
  font-size: 0.9rem;
}
