/* Reutiliza el CSS del layout principal (sidebar + main-content) */

.container {
  display: flex;
  height: 100vh; /* Full viewport height */
}

/* El estilo del sidebar está en patient-sidebar-menu.component.css */


.main-content {
  flex-grow: 1; /* Ocupa el espacio restante */
  padding: 20px;
  background-color: #ecf0f1; /* Fondo claro */
  overflow-y: auto; /* Scroll si el contenido es largo */
}

.welcome-title {
    font-size: 28px;
    color: #333;
    margin-top: 0;
    margin-bottom: 30px;
}

/* --- Estilos para las tarjetas de acceso rápido --- */
.cards-container {
    display: flex; /* Usa flexbox para colocar las tarjetas en una fila */
    gap: 20px; /* Espacio entre tarjetas */
    margin-bottom: 40px;
    flex-wrap: wrap; /* Permite que las tarjetas salten de línea en pantallas pequeñas */
}

.card {
    background-color: white;
    border-radius: 8px;
    padding: 20px;
    text-align: center;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
    flex: 1; /* Permite que las tarjetas se expandan */
    min-width: 180px; /* Ancho mínimo para evitar que se hagan demasiado pequeñas */
    max-width: 220px; /* Ancho máximo */
    position: relative; /* Necesario para posicionar .card-link */
    cursor: pointer; /* Indica que la tarjeta es clickeable */
    transition: transform 0.3s ease, box-shadow 0.3s ease; /* Transición al pasar el ratón */
}

.card:hover {
    transform: translateY(-5px); /* Efecto de elevación */
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.15);
}

.card-icon {
    font-size: 40px; /* Tamaño de los iconos */
    margin-bottom: 10px;
    color: #3498db; /* Color de los iconos */
}

.card-title {
    font-size: 18px;
    font-weight: bold;
    color: #333;
    margin-bottom: 5px;
}

.card-description {
    font-size: 14px;
    color: #555;
}

/* Hace que el link cubra toda la tarjeta */
.card-link {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    text-decoration: none; /* Elimina el subrayado del link */
    z-index: 1; /* Asegura que el link esté por encima del contenido para ser clickeable */
}


/* --- Estilos para la sección de Próxima Cita --- */
.next-appointment-container {
    background-color: white;
    border-radius: 8px;
    padding: 20px;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
}

.next-appointment-container h3 {
    font-size: 22px;
    color: #333;
    margin-top: 0;
    margin-bottom: 20px;
    padding-bottom: 10px;
    border-bottom: 1px solid #eee; /* Línea separadora */
}

.appointment-details {
    display: grid; /* Usa grid para organizar los detalles */
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr)); /* Columnas responsivas */
    gap: 20px; /* Espacio entre elementos de la grid */
    margin-bottom: 20px;
}

.detail-item {
    display: flex;
    flex-direction: column; /* Apila label y value */
}

.detail-label {
    font-size: 14px;
    color: #555;
    margin-bottom: 4px;
    font-weight: bold;
}

.detail-value {
    font-size: 16px;
    color: #333;
}

.appointment-actions {
    display: flex; /* Usa flexbox para los botones de acción */
    gap: 15px; /* Espacio entre botones */
    margin-top: 20px;
}

/* Estilos para los botones de acción (Reagendar/Cancelar) */
.action-button {
    padding: 10px 20px;
    border: none;
    border-radius: 5px;
    cursor: pointer;
    font-size: 15px;
    transition: background-color 0.3s ease;
}

.reschedule-button {
    background-color: #3498db; /* Azul para Reagendar */
    color: white;
}

.reschedule-button:hover {
    background-color: #2980b9;
}

.cancel-button {
    background-color: #e74c3c; /* Rojo para Cancelar */
    color: white;
}

.cancel-button:hover {
    background-color: #c0392b;
}

