@@ -0,0 +1,482 @@
+/* patient-sidebar-menu.component.css */
+
+/* --------------------------------------------------- */
+/* Estilos Base del Sidebar */
+/* --------------------------------------------------- */
+.sidebar {
+    background-color: #1e3a5f; /* Color de fondo */
+    color: #fff;
+    padding: 20px;
+    flex-shrink: 0; /* Evita que el sidebar se encoja */
+
+    /* Posicionamiento fijo para que siempre esté visible */
+    position: fixed;
+    top: 0;
+    left: 0;
+    bottom: 0;
+    height: 100vh; /* Ocupa toda la altura de la ventana */
+    z-index: 100; /* Asegura que esté por encima de otros elementos */
+    overflow-y: auto; /* Permite desplazamiento si el contenido es largo */
+    box-sizing: border-box; /* Incluye padding y borde en el ancho/alto */
+    display: flex;
+    flex-direction: column; /* Organiza los elementos verticalmente */
+    align-items: flex-start; /* Alinea los elementos a la izquierda */
+
+    /* Transiciones suaves para los cambios de ancho y posición */
+    transition: width 0.3s ease-in-out, transform 0.3s ease-in-out, padding 0.3s ease-in-out; /* Añadida transición para padding */
+
+    /* Ancho por defecto para escritorios grandes */
+    width: 400px;
+}
+
+/* --------------------------------------------------- */
+/* Estados del Sidebar (controlados por las clases de Angular) */
+/* --------------------------------------------------- */
+
+/* Estado: Sidebar COMPLETAMENTE OCULTO (hidden) */
+.sidebar.hidden {
+    transform: translateX(-100%); /* Desliza el sidebar completamente fuera de la vista */
+    width: 0; /* No ocupa ancho */
+    padding: 0; /* No tiene padding */
+    overflow: hidden; /* Oculta cualquier contenido que se desborde */
+}
+
+/* Estado: Sidebar COLAPSADO (collapsed) */
+.sidebar.collapsed {
+    width: 60px; /* Ancho reducido para el estado colapsado */
+    padding: 20px 0.5em; /* Ajuste de padding para colapsado */
+    align-items: center; /* Centra los ítems al colapsar */
+}
+
+/* Estado: Sidebar VISIBLE EN MÓVIL (visible-mobile) - para cuando se abre en pantallas pequeñas */
+@media (max-width: 767px) {
+    .sidebar.visible-mobile {
+        transform: translateX(0); /* Desliza el sidebar para hacerlo visible */
+        /* Puedes añadir un fondo oscuro semi-transparente para un efecto de overlay */
+        background-color: rgba(30, 58, 95, 0.95);
+    }
+}
+
+/* --------------------------------------------------- */
+/* Estilos del Botón de Alternancia (Toggle Button) DENTRO del Sidebar */
+/* --------------------------------------------------- */
+.sidebar-toggle-button {
+    background: none;
+    border: none;
+    color: #fff;
+    font-size: 2.2rem; /* Tamaño del icono */
+    cursor: pointer;
+    padding: 10px;
+    align-self: flex-end; /* Alinea el botón a la derecha dentro del sidebar */
+    margin-bottom: 20px;
+    transition: color 0.3s ease;
+    /* Por defecto, visible en escritorio y tablet, oculto en móvil */
+    display: flex; /* Asegura que sea un flex item */
+}
+
+.sidebar-toggle-button:hover {
+    color: #a9d9ff; /* Ligero cambio de color al pasar el ratón */
+}
+
+/* --------------------------------------------------- */
+/* NUEVOS ESTILOS: Botón de Menú Flotante para Móviles (FUERA del Sidebar) */
+/* --------------------------------------------------- */
+.mobile-menu-button-container {
+    position: fixed; /* Fijo en la pantalla */
+    top: 15px; /* Ajusta la posición vertical */
+    left: 15px; /* Ajusta la posición horizontal */
+    z-index: 101; /* Asegura que esté por encima del sidebar y otros elementos */
+    /* La visibilidad en móvil se controla con *ngIf en el HTML */
+    display: none; /* Por defecto oculto, se mostrará solo en móvil vía media query */
+}
+
+.mobile-toggle-button {
+    background-color: #1e3a5f; /* Color de fondo del botón */
+    color: #fff;
+    border: none;
+    border-radius: 50%; /* Lo hace circular */
+    width: 50px; /* Ancho del botón */
+    height: 50px; /* Alto del botón */
+    display: flex;
+    justify-content: center;
+    align-items: center;
+    font-size: 2.2rem; /* Tamaño del icono */
+    cursor: pointer;
+    box-shadow: 0px 2px 10px rgba(0, 0, 0, 0.3); /* Sombra para que se vea flotante */
+    transition: background-color 0.3s ease;
+}
+
+.mobile-toggle-button:hover {
+    background-color: #445c77;
+}
+
+
+/* --------------------------------------------------- */
+/* Estilos del Logo */
+/* --------------------------------------------------- */
+.sidebar .logo {
+    font-size: 2.2rem;
+    font-weight: bold;
+    text-align: center;
+    margin-bottom: 20px;
+    width: 100%;
+    white-space: nowrap; /* Evita que el texto salte de línea */
+    overflow: hidden; /* Oculta el texto que se desborde */
+    text-overflow: ellipsis; /* Añade puntos suspensivos si el texto es muy largo */
+}
+
+/* Ocultar el texto del logo cuando el sidebar está colapsado.
+   Esto solo funciona si el texto "MEDIC" está dentro de un <span>. */
+.sidebar.collapsed .logo span {
+    display: none;
+}
+
+/* --------------------------------------------------- */
+/* Estilos de Navegación Principal */
+/* --------------------------------------------------- */
+.sidebar .main-nav {
+    flex-grow: 1; /* Permite que la navegación ocupe el espacio restante */
+    width: 100%;
+}
+
+.sidebar .main-nav ul {
+    list-style: none;
+    padding: 0;
+    margin: 0;
+    width: 100%;
+}
+
+.sidebar .main-nav ul li {
+    margin-bottom: 15px;
+}
+
+.sidebar .main-nav ul li a {
+    color: #fff;
+    text-decoration: none;
+    display: flex;
+    align-items: center;
+    font-size: 1.3rem; /* Texto más grande */
+    padding: 15px 20px; /* Más espacio para un clic más cómodo */
+    border-radius: 8px;
+    transition: background 0.3s, color 0.3s, padding 0.3s ease-in-out; /* Añadida transición para padding */
+    width: 100%;
+    box-sizing: border-box;
+    white-space: nowrap; /* Evita que el texto salte a la siguiente línea */
+    overflow: hidden; /* Oculta el texto que se desborde */
+    text-overflow: ellipsis; /* Añade puntos suspensivos */
+}
+
+/* Iconos más grandes */
+.sidebar .main-nav ul li a .icon {
+    margin-right: 15px;
+    font-size: 2.5rem;
+    transition: margin-right 0.3s ease-in-out; /* Transición para el margen del icono */
+}
+
+/* Hover y estado activo */
+.sidebar .main-nav ul li a:hover,
+.sidebar .main-nav ul li a.active {
+    background-color: #445c77;
+    color: #fff;
+}
+
+/* Ocultar el texto de los enlaces cuando el sidebar está colapsado */
+.sidebar.collapsed .main-nav ul li a span:not(.icon) {
+    display: none;
+}
+
+/* Ajuste de padding y justificación para enlaces colapsados */
+.sidebar.collapsed .main-nav ul li a {
+    padding: 1em 0.5em; /* Padding ajustado para centrar íconos */
+    justify-content: center; /* Centra el contenido */
+}
+
+/* Eliminar margen del icono cuando el sidebar está colapsado */
+.sidebar.collapsed .main-nav ul li a .icon {
+    margin-right: 0;
+}
+
+
+/* --------------------------------------------------- */
+/* Estilos de Cerrar Sesión */
+/* --------------------------------------------------- */
+.sidebar .logout {
+    margin-top: auto; /* Empuja el botón de logout al final del sidebar */
+    width: 100%;
+    padding-top: 20px; /* Espacio superior antes del borde */
+    border-top: 1px solid rgba(255, 255, 255, 0.1); /* Separador */
+}
+
+.sidebar .logout a {
+    color: #f8d7da; /* Color de texto para logout */
+    text-decoration: none;
+    display: flex;
+    align-items: center;
+    font-size: 1.3rem;
+    padding: 15px 20px;
+    border-radius: 8px;
+    transition: background 0.3s, color 0.3s, padding 0.3s ease-in-out; /* Añadida transición para padding */
+    width: 100%;
+    box-sizing: border-box;
+    white-space: nowrap; /* Evita que el texto salte a la siguiente línea */
+    overflow: hidden; /* Oculta el texto que se desborde */
+    text-overflow: ellipsis; /* Añade puntos suspensivos */
+}
+
+/* Icono de logout más grande */
+.sidebar .logout a .icon {
+    margin-right: 15px;
+    font-size: 2.5rem;
+    transition: margin-right 0.3s ease-in-out; /* Transición para el margen del icono */
+}
+
+/* Hover para logout */
+.sidebar .logout a:hover {
+    background-color: #c0392b; /* Color de fondo al pasar el ratón */
+    color: #fff;
+}
+
+/* Ocultar el texto de cerrar sesión cuando el sidebar está colapsado */
+.sidebar.collapsed .logout a span:not(.icon) {
+    display: none;
+}
+
+/* Ajuste de padding y justificación para enlaces colapsados */
+.sidebar.collapsed .logout a {
+    padding: 1em 0.5em; /* Padding ajustado para centrar íconos */
+    justify-content: center; /* Centra el contenido */
+}
+
+/* Eliminar margen del icono cuando el sidebar está colapsado */
+.sidebar.collapsed .logout a .icon {
+    margin-right: 0;
+}
+
+
+/* --------------------------------------------------- */
+/* Estilos del Contenido Principal (main-content) */
+/* --------------------------------------------------- */
+.main-content {
+    /* Margen inicial para dejar espacio al sidebar expandido (400px) */
+    margin-left: 400px;
+    padding: 20px;
+    transition: margin-left 0.3s ease-in-out, width 0.3s ease-in-out; /* Transición suave del margen y ancho */
+    box-sizing: border-box;
+    width: calc(100% - 400px); /* Ocupa el resto del ancho */
+    min-height: 100vh; /* Asegura que ocupe al menos la altura de la ventana */
+}
+
+/* Ajuste del main-content cuando el sidebar está oculto */
+.main-content.sidebar-hidden {
+    margin-left: 0; /* No hay margen si el sidebar está completamente oculto */
+    width: 100%; /* Ocupa todo el ancho */
+}
+
+/* Ajuste del main-content cuando el sidebar está colapsado */
+.main-content.sidebar-collapsed {
+    margin-left: 60px; /* Margen reducido al ancho del sidebar colapsado */
+    width: calc(100% - 60px); /* Ocupa el resto del ancho */
+}
+
+/* --------------------------------------------------- */
+/* Media Queries para Responsividad */
+/* --------------------------------------------------- */
+
+/* Móviles (menos de 768px) */
+@media (max-width: 767px) {
+    .sidebar {
+        width: 280px; /* Un ancho más razonable para el sidebar en móvil */
+        transform: translateX(-100%); /* Oculto por defecto en móviles */
+        box-shadow: 2px 0 10px rgba(0, 0, 0, 0.4); /* Sombra más pronunciada en móvil */
+        padding: 15px; /* Ajusta el padding */
+    }
+
+    .sidebar.hidden {
+        /* Ya definido arriba, pero se asegura que la transformación se aplica */
+        transform: translateX(-100%);
+    }
+
+    .sidebar.visible-mobile {
+        transform: translateX(0); /* Muestra el sidebar en móvil */
+    }
+
+    /* El main-content siempre ocupa todo el ancho en móvil */
+    .main-content {
+        margin-left: 0;
+        width: 100%;
+        padding: 15px; /* Ajusta el padding para móvil */
+    }
+
+    /* Oculta el botón de alternancia original del sidebar en móvil,
+       ya que usamos el botón flotante */
+    .sidebar-toggle-button {
+        display: none;
+    }
+
+    /* Muestra el botón de menú móvil en este rango */
+    .mobile-menu-button-container {
+        display: flex; /* Asegura que sea visible solo en móvil */
+    }
+}
+
+/* Tabletas y Escritorios Pequeños (entre 768px y 1024px) */
+@media (min-width: 768px) and (max-width: 1024px) {
+    .sidebar {
+        width: 60px; /* Por defecto colapsado en este rango */
+        padding: 20px 0.5em; /* Ajusta el padding para el estado colapsado */
+        align-items: center; /* Centra los ítems al colapsar */
+    }
+
+    /* En este rango, el sidebar siempre estará colapsado por defecto,
+       así que estas reglas aseguran que los elementos internos se comporten como tal. */
+    .sidebar .logo span,
+    .sidebar .main-nav ul li a span:not(.icon),
+    .sidebar .logout a span:not(.icon) {
+        display: none; /* Oculta el texto */
+    }
+
+    .sidebar .main-nav ul li a,
+    .sidebar .logout a {
+        padding: 1em 0.5em; /* Ajusta padding */
+        justify-content: center; /* Centra iconos */
+    }
+
+    .sidebar .main-nav ul li a .icon,
+    .sidebar .logout a .icon {
+        margin-right: 0; /* Elimina margen de iconos */
+    }
+
+    .sidebar.collapsed {
+        width: 60px; /* Asegura que esté colapsado */
+        transform: translateX(0); /* Asegura que esté visible si no está oculto */
+        padding: 20px 0.5em; /* Mantiene el padding para colapsado */
+    }
+
+    .sidebar.hidden {
+        transform: translateX(-100%); /* Permite ocultar completamente si el usuario lo activa */
+        width: 0;
+        padding: 0;
+    }
+
+    /* Ajuste del contenido principal para el sidebar colapsado */
+    .main-content {
+        margin-left: 60px;
+        width: calc(100% - 60px);
+        padding: 20px;
+    }
+
+    /* Mostrar el botón de alternancia original del sidebar en este rango */
+    .sidebar-toggle-button {
+        display: flex;
+        align-self: flex-end;
+    }
+
+    /* Oculta el botón de menú móvil en este rango */
+    .mobile-menu-button-container {
+        display: none;
+    }
+}
+
+/* Escritorios Grandes (más de 1024px) */
+@media (min-width: 1025px) {
+    .sidebar {
+        width: 400px; /* Por defecto expandido */
+        padding: 20px; /* Padding por defecto expandido */
+        align-items: flex-start; /* Alinea los ítems a la izquierda cuando está expandido */
+    }
+
+    .sidebar.collapsed {
+        width: 60px; /* Permite colapsar */
+        padding: 20px 0.5em; /* Padding para el estado colapsado */
+        align-items: center; /* Centra los ítems al colapsar */
+    }
+
+    .sidebar.hidden {
+        transform: translateX(-100%); /* Permite ocultar */
+        width: 0;
+        padding: 0;
+    }
+
+    /* Ajuste del contenido principal */
+    .main-content {
+        margin-left: 400px; /* Margen para sidebar expandido */
+        width: calc(100% - 400px);
+    }
+
+    .main-content.sidebar-collapsed {
+        margin-left: 60px; /* Margen para sidebar colapsado */
+        width: calc(100% - 60px);
+    }
+
+    .main-content.sidebar-hidden {
+        margin-left: 0; /* Margen para sidebar oculto */
+        width: 100%;
+    }
+
+    /* Mostrar texto por defecto en escritorio grande */
+    .sidebar .logo span,
+    .sidebar .main-nav ul li a span:not(.icon),
+    .sidebar .logout a span:not(.icon) {
+        display: inline; /* O block/flex según necesites, inline es común para texto junto a íconos */
+    }
+
+    /* Ocultar texto cuando está colapsado en escritorio grande */
+    .sidebar.collapsed .logo span,
+    .sidebar.collapsed .main-nav ul li a span:not(.icon),
+    .sidebar.collapsed .logout a span:not(.icon) {
+        display: none;
+    }
+
+    /* Ajuste de padding y justificación para enlaces colapsados en escritorio grande */
+    .sidebar.collapsed .main-nav ul li a,
+    .sidebar.collapsed .logout a {
+        padding: 1em 0.5em;
+        justify-content: center;
+    }
+
+    /* Eliminar margen del icono cuando el sidebar está colapsado en escritorio grande */
+    .sidebar.collapsed .main-nav ul li a .icon,
+    .sidebar.collapsed .logout a .icon {
+        margin-right: 0;
+    }
+
+    /* Oculta el botón de menú móvil en este rango */
+    .mobile-menu-button-container {
+        display: none;
+    }
+}
+
+/* --------------------------------------------------- */
+/* Estilos para los íconos (emojis o Material Icons) */
+/* --------------------------------------------------- */
+/* Si usas Material Icons, asegúrate de que la fuente esté importada en tu index.html */
+.material-icons {
+    font-family: 'Material Icons';
+    font-weight: normal;
+    font-style: normal;
+    font-size: 24px; /* Tamaño por defecto para los iconos de Material */
+    line-height: 1;
+    letter-spacing: normal;
+    text-transform: none;
+    display: inline-block;
+    white-space: nowrap;
+    word-wrap: normal;
+    direction: ltr;
+
+    -webkit-font-smoothing: antialiased;
+}
+
+/* Ajuste para los iconos dentro de los enlaces de navegación */
+.sidebar .main-nav ul li a .icon,
+.sidebar .logout a .icon {
+    /* Estos son tus iconos de emoji o personalizados, no Material Icons */
+    /* Mantén tus estilos originales si son emojis o SVG */
+    font-size: 2.5rem; /* Tamaño para tus iconos específicos */
+    margin-right: 15px;
+}
+
+/* Ajuste para el tamaño de los iconos de Material en los botones de alternancia */
+.sidebar-toggle-button .material-icons,
+.mobile-toggle-button .material-icons {
+    font-size: 2.2rem; /* Asegura que el icono del botón tenga el tamaño deseado */
+}
