/* Reutiliza los estilos del menú del doctor. Ajusta si es necesario */

.sidebar {
  width: 250px; /* Ancho del menú lateral */
  background-color: #2c3e50; /* Color de fondo oscuro */
  color: white;
  padding: 20px;
  display: flex;
  flex-direction: column;
  justify-content: space-between; /* Empuja el logout hacia abajo */
  height: 100vh; /* O la altura necesaria */
  box-sizing: border-box; /* Incluye padding y borde en el tamaño total */
}

.logo {
  font-size: 24px;
  font-weight: bold;
  text-align: center;
  margin-bottom: 40px;
  color: #3498db; /* Color del logo (azul) */
}

.main-nav ul {
  list-style: none;
  padding: 0;
  margin: 0;
}

.main-nav li {
  margin-bottom: 15px; /* Espacio entre elementos del menú */
}

.main-nav a {
  color: white;
  text-decoration: none;
  font-size: 16px;
  display: flex;
  align-items: center;
  padding: 10px;
  border-radius: 4px;
  transition: background-color 0.3s ease; /* Transición suave al pasar el ratón */
}

.main-nav a .icon {
  margin-right: 10px;
  font-size: 18px; /* Tamaño de los iconos (ajusta si usas Font Awesome) */
  /* Estilos para los iconos de texto si los usas */
  display: inline-block;
  width: 20px; /* Ancho fijo para alinear iconos */
  text-align: center;
}

.main-nav a:hover,
.main-nav a.active { /* Estilo para el link activo */
  background-color: #34495e; /* Color de fondo al pasar el ratón o estar activo */
}

.logout {
    margin-top: auto; /* Empuja el logout al final si el sidebar tiene flex-direction: column */
}

.logout a {
  color: white;
  text-decoration: none;
  font-size: 16px;
  display: flex;
  align-items: center;
  padding: 10px;
  border-radius: 4px;
  transition: background-color 0.3s ease;
}

.logout a .icon {
    margin-right: 10px;
    font-size: 18px;
     display: inline-block;
    width: 20px; /* Ancho fijo para alinear iconos */
    text-align: center;
}


.logout a:hover {
    background-color: #e74c3c; /* Color de fondo rojo al pasar el ratón */
}

/* Si usas Font Awesome, elimina los estilos .icon de span y usa los propios de FA */
