commits.content:
.register-container {
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background-color: #f0f2f5;
}

.register-card {
  background-color: #fff;
  padding: 30px;
  border-radius: 8px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  width: 500px;
  max-width: 90%;
}

.register-header {
  text-align: center;
  margin-bottom: 25px;
}

.register-header h1 {
  color: #1877f2;
  margin-bottom: 5px;
}

.register-header p {
  color: #657786;
  font-size: 0.9em;
}

.register-form {
  display: flex;
  flex-direction: column;
}

.form-row {
  display: flex;
  gap: 20px;
  margin-bottom: 20px;
}

.form-group {
  flex: 1;
  margin-bottom: 20px;
}

.form-group label {
  display: block;
  margin-bottom: 5px;
  color: #1c1e21;
  font-weight: bold;
  font-size: 0.95em;
}

.form-group input[type="text"],
.form-group input[type="email"],
.form-group input[type="tel"],
.form-group input[type="password"] {
  width: 100%;
  padding: 10px;
  border: 1px solid #ddd;
  border-radius: 6px;
  font-size: 1em;
  box-sizing: border-box;
}

.date-input-container {
  position: relative;
  display: flex;
  align-items: center;
}

.date-input-container input[type="text"] {
  width: 100%;
  padding-right: 35px; /* Espacio para el icono del calendario */
}

.calendar-button {
  position: absolute;
  right: 5px;
  top: 50%;
  transform: translateY(-50%);
  background: none;
  border: none;
  cursor: pointer;
  color: #657786;
}

.calendar-icon {
  width: 20px;
  height: 20px;
}

.password-input-container {
  position: relative;
  display: flex;
  align-items: center;
}

.password-input-container input[type="password"] {
  padding-right: 35px; /* Espacio para el bot√≥n de mostrar/ocultar */
}

.password-toggle-button {
  position: absolute;
  right: 5px;
  top: 50%;
  transform: translateY(-50%);
  background: none;
  border: none;
  cursor: pointer;
  color: #657786;
  font-size: 1.1em;
}

.accept-terms {
  display: flex;
  align-items: center;
  margin-bottom: 20px;
  font-size: 0.9em;
}

.accept-terms input[type="checkbox"] {
  margin-right: 8px;
}

.terms-link {
  color: #1877f2;
  text-decoration: none;
}

.terms-link:hover {
  text-decoration: underline;
}

.register-button {
  background-color: #1877f2;
  color: #fff;
  padding: 12px;
  border: none;
  border-radius: 6px;
  font-size: 1.1em;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.register-button:hover {
  background-color: #1665c8;
}

.register-footer {
  text-align: center;
  margin-top: 25px;
  font-size: 0.9em;
  color: #657786;
}

.login-link {
  color: #1877f2;
  text-decoration: none;
  font-weight: bold;
}

.login-link:hover {
  text-decoration: underline;
}
