.sidebar {
  background-color: #3f51b5; /* Un azul primario */
  color: white;
  width: 20%; /* Usar porcentaje para adaptarse al ancho */
  min-width: 200px; /* Ancho mínimo para evitar que se comprima demasiado */
  padding: 2em; /* Usar em para escalar con el tamaño de la fuente */
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  position: fixed; /* Fija la posición del menú */
  top: 0; /* Lo pega a la parte superior de la ventana */
  left: 0; /* Lo pega al lado izquierdo de la ventana */
  bottom: 0; /* Asegura que se extienda hasta la parte inferior */
  height: 100vh; /* Ocupa toda la altura de la ventana */
  z-index: 100; /* Asegura que esté por encima de otros elementos si es necesario */
}

.logo {
  font-size: 2em; /* Aumentar el tamaño del logo */
  font-weight: bold;
  margin-bottom: 3em; /* Aumentar el margen inferior */
}

.main-nav ul {
  list-style: none;
  padding: 0;
  margin: 0;
  width: 100%;
}

.main-nav ul li a {
  display: flex;
  align-items: center; /* Centrar verticalmente icono y texto */
  color: white;
  padding: 1.2em 1.8em; /* Aumentar el padding general */
  padding-left: 1em; /* Reducir el padding izquierdo para acercar el texto al icono */
  text-decoration: none;
  margin-bottom: 0.6em;
  border-radius: 0.5em;
  transition: background-color 0.3s ease;
  font-size: 1.2em; /* Mantener el tamaño de la fuente actual */
}

.main-nav ul li a:hover {
  background-color: rgba(255, 255, 255, 0.1);
}

.main-nav ul li a .icon {
  margin-right: 1em;
  font-size: 2.5em; /* Aumentar significativamente el tamaño del icono */
}

.logout {
  margin-top: auto;
}

.logout a {
  display: flex;
  align-items: center; /* Centrar verticalmente icono y texto */
  color: white;
  padding: 1.2em 1.8em;
  padding-left: 1em; /* Reducir el padding izquierdo para acercar el texto al icono */
  text-decoration: none;
  margin-bottom: 0.6em;
  border-radius: 0.5em;
  transition: background-color 0.3s ease;
  font-size: 1.2em;
}

.logout a:hover {
  background-color: rgba(255, 255, 255, 0.1);
}

.logout a .icon {
  margin-right: 1em;
  font-size: 2.5em;
}
