+}
+
+.icon {
+    font-size: 2.5em;
+    margin-bottom: 15px;
+    color: #1976d2; /* Color del icono de paciente */
+}
+
+.doctor-card .icon {
+    color: #4caf50; /* Color del icono de doctor */
+}
+
+.option-card h2 {
+    margin-top: 0;
+    margin-bottom: 10px;
+    color: #343a40; /* Un negro suave */
+}
+
+.option-card p {
+    color: #6c757d;
+    font-size: 0.9em;
+    margin-bottom: 15px;
+}
+
+.access-button {
+    background-color: #e3f2fd; /* Fondo claro para el botón de paciente */
+    color: #1976d2;
+    border: none;
+    border-radius: 4px;
+    padding: 10px 20px;
+    cursor: pointer;
+    font-weight: bold;
+    transition: background-color 0.3s ease;
+}
+
+.access-button:hover {
+    background-color: #bbdefb;
+}
+
+.doctor-card .access-button {
+    background-color: #e8f5e9; /* Fondo claro para el botón de doctor */
+    color: #4caf50;
+}
+
+.doctor-card .access-button:hover {
+    background-color: #c8e6c9;
+}
+
+footer {
+    color: #6c757d;
+    font-size: 0.85em;
+}
